    private void btnAdd1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdd1ActionPerformed
        // TODO add your handling code here:
        for (String s : list1.getSelectedValuesList()) {
            dlm2.addElement(s);
            dlm.remove(list1.getSelectedIndex());
            list2.setModel(dlm2);
        }
    }//GEN-LAST:event_btnAdd1ActionPerformed

    private void btnAddAll1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddAll1ActionPerformed
        // TODO add your handling code here:
        int start = 0;
        int end = list1.getModel().getSize() - 1;
        if (end >= 0) {
            list1.setSelectionInterval(start, end);
        }
        for (String s : list1.getSelectedValuesList()) {
            dlm2.addElement(s);
            dlm.remove(list1.getSelectedIndex());
            list2.setModel(dlm2);
        }
    }//GEN-LAST:event_btnAddAll1ActionPerformed

    private void btnRemove1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRemove1ActionPerformed
        // TODO add your handling code here:
        for (String s : list2.getSelectedValuesList()) {
            dlm.addElement(s);
            dlm2.remove(list2.getSelectedIndex());
            list1.setModel(dlm);
        }
    }//GEN-LAST:event_btnRemove1ActionPerformed

    private void btnRemoveAll1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRemoveAll1ActionPerformed
        // TODO add your handling code here:
        int start = 0;
        int end = list2.getModel().getSize() - 1;
        if (end >= 0) {
            list2.setSelectionInterval(start, end);
        }

        for (String s : list2.getSelectedValuesList()) {
            dlm.addElement(s);
            dlm2.remove(list2.getSelectedIndex());
            list1.setModel(dlm);
        }
    }//GEN-LAST:event_btnRemoveAll1ActionPerformed

    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
        // TODO add your handling code here:
        Product product = network.getProductList().addNewProduct();
        product.setProductName(lblMetalName.getText());
        product.setQuantity(Double.parseDouble(txtFieldMetalQuantity.getText()));
        lblMetalName.setText("Metal Name");
        lblMetalName.setVisible(false);
        txtFieldMetalQuantity.setText("");
        txtFieldMetalQuantity.setVisible(false);
        dlm2.remove(list2.getSelectedIndex());
        JOptionPane.showMessageDialog(this, "Product created successfully");
    }//GEN-LAST:event_btnSaveActionPerformed

